{
    "name": "VIP Tech Enthusiasts",
    "description": "Subscribers with both VIP and Tech Enthusiast tags",
    "filterCriteria": {
        "tags": {
            "contains": [1, 2]
        }
    }
}

{
    "name": "Subscribers Named John",
    "description": "Subscribers with 'John' in their name",
    "filterCriteria": {
        "name": {
            "contains": "John"
        }
    }
}

{
    "name": "Recent Subscribers",
    "description": "Subscribers who joined in the last 30 days",
    "filterCriteria": {
        "subscribed_at": {
            "gte": "2025-04-14T00:00:00Z"
        }
    }
}

{
    "name": "Recent VIPs",
    "description": "VIP subscribers who joined in the last 3 months",
    "filterCriteria": {
        "tags": {
            "contains": [1]
        },
        "subscribed_at": {
            "gte": "2025-02-14T00:00:00Z"
        }
    }
}

{
    "name": "Tech and Sports Fans",
    "description": "Subscribers interested in both tech and sports",
    "filterCriteria": {
        "metadata": {
            "interests": {
                "contains": ["tech", "sports"]
            }
        }
    }
}

{
    "name": "California Tech VIPs",
    "description": "VIP subscribers from California interested in tech who joined in 2025",
    "filterCriteria": {
        "tags": {
            "contains": [1, 2]
        },
        "subscribed_at": {
            "gte": "2025-01-01T00:00:00Z"
        },
        "metadata": {
            "state": {
                "eq": "california"
            },
            "interests": {
                "contains": ["tech"]
            }
        }
    }
}

{
    "name": "Active Subscribers Only",
    "description": "Only currently active subscribers",
    "filterCriteria": {
        "is_active": {
            "eq": true
        }
    }
}

{
    "name": "Subscribers with DOB",
    "description": "Subscribers who have provided their date of birth",
    "filterCriteria": {
        "date_of_birth": {
            "not_null": true
        }
    }
}

{
    "name": "Updated Campaign Name",
    "description": "Updated campaign description",
    "subject": "Updated Subject Line",
    "templateVariables": {
        "offerName": "Summer Sale",
        "discount": "20%",
        "expiryDate": "2023-07-31"
    },
    "mailingListIds": [1, 2]
}

{
    "content": "This is my comment on the blog post.",
    "parentId": null
}
